#' Correlation clustering
#'
#' 
#' @param data .
#' @param vars .
#' @param group .
#' @param selgroup .
#' @param hyp .
#' @param method .
#' @param adjust .
#' @param tables .
#' @param pval .
#' @param flag .
#' @param n .
#' @param ci .
#' @param ciWidth .
#' @param plots \code{TRUE} or \code{FALSE} (default), provide a correlation
#'   matrix plot
#' @param signif .
#' @param plotMetU .
#' @param plotMetL .
#' @param plotOrder .
#' @param hclust .
#' @param numClust .
#' @param clustMet .
#' @param clPos .
#' @param clustCol .
#' @param clustMan .
#' @param clustMat .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$matrix} \tab \tab \tab \tab \tab a correlation matrix table \cr
#'   \code{results$treeplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$rplots} \tab \tab \tab \tab \tab an array of images \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$matrix$asDF}
#'
#' \code{as.data.frame(results$matrix)}
#'
#' @export
mCOR <- function(
    data,
    vars,
    group,
    selgroup,
    hyp = "corr",
    method = "pearson",
    adjust = "none",
    tables = TRUE,
    pval = TRUE,
    flag = FALSE,
    n = FALSE,
    ci = FALSE,
    ciWidth = 95,
    plots = FALSE,
    signif = "all",
    plotMetU = "ellipse",
    plotMetL = "ellipse",
    plotOrder = "original",
    hclust = FALSE,
    numClust = 2,
    clustMet = "ward.D2",
    clPos = "b",
    clustCol = FALSE,
    clustMan = "",
    clustMat = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("mCOR requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- mCOROptions$new(
        vars = vars,
        group = group,
        selgroup = selgroup,
        hyp = hyp,
        method = method,
        adjust = adjust,
        tables = tables,
        pval = pval,
        flag = flag,
        n = n,
        ci = ci,
        ciWidth = ciWidth,
        plots = plots,
        signif = signif,
        plotMetU = plotMetU,
        plotMetL = plotMetL,
        plotOrder = plotOrder,
        hclust = hclust,
        numClust = numClust,
        clustMet = clustMet,
        clPos = clPos,
        clustCol = clustCol,
        clustMan = clustMan,
        clustMat = clustMat)

    analysis <- mCORClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

